mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY test.test_number DESC) AS row_num , participate_number , participate_status , total_score , test_title , exam_date FROM participate JOIN test ON test.test_number = participate.test_number
test.lecture_number = #{lectureNumber}
AND test_title LIKE '%' || #{testTitle} || '%'
AND participate_status = '응시 완료'
AND participate_status = '미응시'
) WHERE row_num BETWEEN #{startRow} AND #{endRow}
