mapper.select:
SELECT * FROM ( SELECT lecture_material_number , title , views , lecture_number , content , registration_date , ROW_NUMBER() OVER (ORDER BY lecture_material_number DESC) AS row_num FROM lecture_material WHERE lecture_number = #{lectureNumber}
AND title LIKE CONCAT('%', #{title}, '%')
) WHERE row_num BETWEEN #{startRow} + 1 AND #{startRow} + #{pageSize}
